import React from "react";
import Product from "../Product/Product";
import { connect } from "react-redux";
import * as actions from "../../store/actions/index";
import styles from "./Products.module.css";
import axios from "axios";

class Products extends React.Component {
  state = {
    products: {},
  };

  componentDidMount() {
    axios
      .get("https://ecommerce-31a69.firebaseio.com/products.json")
      .then((response) => {
        this.setState({
          products: response.data,
        });
      })
      .catch((error) => console.log(error));
  }
  render() {
    const productLine = this.props.match.params.linkUrl;
    let products = null;
    if (this.state.products[productLine]) {
      this.state.products[productLine].map((i) => console.log(i));
      products = this.state.products[productLine].map((product) => {
        return (
          <Product
            key={product.id}
            product={product}
            clicked={this.props.onAddToCart}
          />
        );
      });
    }
    return <div className={styles.Products}>{products}</div>;
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    onAddToCart: (product) => dispatch(actions.addToCart(product)),
  };
};
const mapStateToProps = (state) => {
  return {
    products: state.products,
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Products);
