import React from "react";
import Product from "../Product/Product";
import { connect } from "react-redux";
import * as actions from "../../store/actions/index";
import styles from "./Products.module.css";
import axios from "axios";

class Products extends React.Component {
  state = {
    hats: [
      {
        id: 1,
        name: "blue-hat",
        price: 2,
        img: "https://i.ibb.co/cvpntL1/hats.png",
      },
      {
        id: 2,
        name: "red hat",
        price: 3,
        img: "https://i.ibb.co/cvpntL1/hats.png",
      },
    ],
    sneakers: [
      {
        id: 1,
        name: "sneaker",
        price: 3,
        img: "https://i.ibb.co/cvpntL1/hats.png",
      },
    ],
    jackets: [
      {
        id: 1,
        name: "ao khoac",
        price: 3,
        img: "https://i.ibb.co/cvpntL1/hats.png",
      },
    ],
  };

  componentDidMount() {
    axios
      .get("https://ecommerce-31a69.firebaseio.com/products.json")
      .then((response) => {
        this.setState({
          products: response.data,
        });
      })
      .catch((error) => console.log(error));
  }
  render() {
    const productLine = this.props.match.params.linkUrl;
    let products = this.state[productLine].map((product) => {
      return (
        <Product
          key={product.id}
          product={product}
          clicked={this.props.onAddToCart}
        />
      );
    });
    return <div className={styles.Products}>{products}</div>;
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    onAddToCart: (product) => dispatch(actions.addToCart(product)),
  };
};
export default connect(null, mapDispatchToProps)(Products);
