const initialState = {
  cart: [],
  quantity: 0,
  totalPrice: 0,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TO_CART":
      const cart = state.cart;
      const newQuantity = state.quantity + 1;
      if (cart.indexOf(action.product.id)) console.log(cart);
      return {
        ...state,
        cart,
        newQuantity,
      };
    default:
      return {
        ...state,
      };
  }
};
export default reducer;
